import{_ as a,c as s,o as r,V as i}from"./chunks/framework.966e8369.js";const p=JSON.parse('{"title":"Spurt og svarað","description":"","frontmatter":{"aside":false,"prev":{"text":"Tilbaka","link":"/index.md"}},"headers":[],"relativePath":"faq.md","filePath":"faq.md"}'),n={name:"faq.md"},t=i('<h1 id="spurt-og-svarað" tabindex="-1">Spurt og svarað <a class="header-anchor" href="#spurt-og-svarað" aria-label="Permalink to &quot;Spurt og svarað&quot;">​</a></h1><h2 id="logskraningar" tabindex="-1">Lögskráningar <a class="header-anchor" href="#logskraningar" aria-label="Permalink to &quot;Lögskráningar&quot;">​</a></h2><details class="details custom-block"><summary>Ég fæ villuboð þegar ég skrái mig inn með ísland.is</summary><p>Ef ekki tekst að skrá sig með rafrænum skilríkjum sendið þá <a href="https://www.samgongustofa.is/samband/" target="_blank" rel="noreferrer">fyrirspurn hér</a>.</p></details><details class="details custom-block"><summary>Hvar sæki ég um aðgang að Skútunni til þess að geta lögskráð?</summary><p>Senda má fyrirspurnir um lögskráningu sjómanna og aðgang að lögskráningarhluta Skútunnar hér <a href="mailto:logskraning@samgongustofa.is" target="_blank" rel="noreferrer">logskraning@samgongustofa.is</a>.</p></details><details class="details custom-block"><summary>Hvar sæki ég um frest fyrir öryggisfræðslu?</summary><p><a href="https://www.samgongustofa.is/siglingar/monnun-og-skraning/logskraningar/slysavarnarskoli-frestur/" target="_blank" rel="noreferrer">Umsókn um frest </a></p></details><details class="details custom-block"><summary>Hver eru skilyrðin til lögskráningar?</summary><ul><li>Skipið hafi gilt haffærisskírteini.</li><li>Áhafnatrygging sé í gildi.</li><li>Skipstjórnarmenn hafi gild atvinnuskírteini</li><li>Vélstjórnarmenn hafi gild atvinnuskírteini</li></ul><p>Allir í áhöfn hafi lokið a.m.k. smábátanámskeiði hjá <strong>Slysavarnaskóla sjómanna</strong> og endurnýjað það á fimm ára fresti.</p><p>Þó geta þeir fengið lögskráningu sem hafa innan við <strong>180 lögskráða daga</strong> í Skútunni og er miðað við að þeir nýti þá daga til að taka tilskilin öryggisfræðslunámskeið í Slysavarnaskóla sjómanna.</p></details><details class="details custom-block"><summary>Undanþága til starfa á fiskiskipum og öðrum skipum en farþegaskipum og flutningaskipum</summary><p>Í einstaka tilvikum eru mönnum veittar undanþágur til að gegna stöðu á skipi þrátt fyrir að tilskilin réttindi vanti.</p><p><a href="https://eydublod.samgongustofa.is/27635894850265002716" target="_blank" rel="noreferrer">Umsókn um undanþágu</a></p></details>',7),e=[t];function l(g,o,m,u,k,f){return r(),s("div",null,e)}const c=a(n,[["render",l]]);export{p as __pageData,c as default};
