import{_ as a,a as r,b as s}from"./chunks/icon_warn.ed806f44.js";import{_ as i,c as n,o as t,V as e}from"./chunks/framework.8b890a66.js";const v=JSON.parse('{"title":"Lögskráningar á fiskiskip og önnur skip <12m. og 250-750kW","description":"","frontmatter":{},"headers":[],"relativePath":"logskraning/fiski-u12.md"}'),g={name:"logskraning/fiski-u12.md"},l=e('<h1 id="logskraningar-a-fiskiskip-og-onnur-skip-12m-og-250-750kw" tabindex="-1">Lögskráningar á fiskiskip og önnur skip &lt;12m. og 250-750kW <a class="header-anchor" href="#logskraningar-a-fiskiskip-og-onnur-skip-12m-og-250-750kw" aria-label="Permalink to &quot;Lögskráningar á fiskiskip og önnur skip &lt;12m. og 250-750kW&quot;">​</a></h1><p>Til að hægt sé að lögskrá þarf skipið að vera komið með <strong>haffærisskírteini</strong> og <strong>áhafnartryggingu</strong> fyrir alla um borð, einstaklingur <strong>þarf að hafa réttindi</strong> í þá stöðu sem lögskrá á hann í. Hann þarf að hafa lokið amk. <strong>öryggisfræðslu smábáta</strong> (T1) hjá Slysavarnaskóla sjómanna og <strong>endurnýjað hana</strong> (T2) á 5 ára fresti.</p><h2 id="eftirfarandi-tilkynningar-birtast-þegar-reynt-er-að-logskra" tabindex="-1">Eftirfarandi tilkynningar birtast þegar reynt er að lögskrá: <a class="header-anchor" href="#eftirfarandi-tilkynningar-birtast-þegar-reynt-er-að-logskra" aria-label="Permalink to &quot;Eftirfarandi tilkynningar birtast þegar reynt er að lögskrá:&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">á skip sem hefur ekki gilt haffærisskírteini</p><p><span style="background:#F7DD92;color:black;padding:4px 10px;border-radius:6px;">Haffærisskírteini ekki í gildi</span></p><p>Sækja þarf um útgáfu haffærisskírteinis – eftir að skoðanir hafa farið fram.</p><p><a href="https://eydublod.samgongustofa.is/20635888850250541156" target="_blank" rel="noreferrer">Umsókn um útgáfu haffærisskírteinis</a></p><p><a href="https://www.samgongustofa.is/siglingar/krofur-til-skipa/haffaeri-og-skodun" target="_blank" rel="noreferrer">Nánar um haffæri og skoðun</a></p><p>Einnig má hafa samband við ísafjörð ef sækja þarf um frest <a href="mailto:isafjordur@samgongustofa.is" target="_blank" rel="noreferrer">Umsókn um frest</a></p></div><div class="info custom-block"><p class="custom-block-title">á skip sem hefur ekki gilda áhafnartryggingu:</p><p><span style="background:#F7DD92;color:black;padding:10px;border-radius:6px;">Engin áhafnatrygging er í gildi fyrir þetta skip</span></p><p>Hafið samband við tryggingarfélag útgerðar</p></div><div class="info custom-block"><p class="custom-block-title">fleiri um borð en áhafnatrygging miðar við:</p><p><span style="background:#F7DD92;color:black;padding:10px;border-radius:6px;">Ekki áhafnatrygging fyrir fleiri á viðkomandi skipi</span></p><p>Hafið samband við tryggingarfélag útgerðar.</p></div><div class="info custom-block"><p class="custom-block-title">einstakling í stöðu sem hann hefur ekki réttindi til að gegna:</p><p><span style="background:#F7DD92;color:black;padding:10px;border-radius:6px;">Hefur ekki gilt námskeið í hóp- og neyðarstjórnun farþega</span></p><p>Sækja þarf um atvinnuréttindi eða endurnýja réttindi sem eru útrunnin</p><p><a href="https://innskraning.island.is/?id=eydubl.samgongustofa.is&amp;AuthID=6caee0c5-67d3-4076-b477-d67f4d00df14" target="_blank" rel="noreferrer">Umsókn vegna útgáfu eða endurnýjunar atvinnuskírteinis</a></p></div><div class="info custom-block"><p class="custom-block-title">einstakling sem ekki hefur lokið öryggisfræðslu smábáta:</p><p><span style="background:#F7DD92;color:black;padding:10px;border-radius:6px;">Hefur ekki lokið öryggisfræðslu smábáta</span></p><p>viðkomandi þarf að skrá sig í slysavarnaskólann og sækja um frest hér</p><p><a href="https://eydublod.samgongustofa.is/26635894123012325331" target="_blank" rel="noreferrer">Umsókn um frest</a></p></div><div class="info custom-block"><p class="custom-block-title">einstakling sem ekki hefur endurnýjað öryggisfræðslu smábáta:</p><p><span style="background:#F7DD92;color:black;padding:10px;border-radius:6px;">Hefur ekki lokið endurmenntun á öryggisfræðslu smábáta</span></p><ul><li>Viðkomandi þarf að skrá sig í slysavarnaskólann og sækja um frest hér</li></ul><p><a href="https://eydublod.samgongustofa.is/26635894123012325331" target="_blank" rel="noreferrer">Umsókn um frest</a></p></div><div class="info custom-block"><p class="custom-block-title">einstaklingur er lögskráður á annað skip:</p><p><span style="background:#F7DD92;color:black;padding:10px;border-radius:6px;">Einstaklingur er/var lögskráður á annað skip (skipaskrárnúmer) á gefinni dagsetningu lögskráningar</span></p><ul><li>Hafa þarf samband við útgerð skipsins sem eintstaklingur er lögskráður á eða senda t.p. til lögskráningardeild Samgöngustofu</li></ul><p><a href="mailto:logskraning@samgongustofa.is" target="_blank" rel="noreferrer">Afskrá einstakling</a></p></div><br><br><br><h2 id="lagmarksmonnun-þegar-utivist-er-innan-við-14-klst" tabindex="-1">Lágmarksmönnun þegar útivist er innan við 14 klst.: <a class="header-anchor" href="#lagmarksmonnun-þegar-utivist-er-innan-við-14-klst" aria-label="Permalink to &quot;Lágmarksmönnun þegar útivist er innan við 14 klst.:&quot;">​</a></h2><p>Á fiskiskipum og öðrum skipum 12m. og styttri að skráningarlengd og með aðalvél 250-750 kW. þarf skipstjóra og smáskipavörð um borð. Skipstjóri má einnig gegna störfum smáskipavélavarðar hafi hann þau réttindi, að öðrum kosti þarf auk þess að lögskrá smáskipavélavörð</p><div class="info custom-block"><p class="custom-block-title">vélarafl undir 250 kW</p><p>ekki þarf smáskipavélavörð (SSV) ef vélarafl skipsins er 249 kW eða minna:</p><p>Ekki þarf smáskipavélavörð (SSV) ef gerður hefur verið samningur við þjónustuaðila um viðhald vélbúnaðar skipsins og sá samingur er staðfestur af Samgöngustofu.</p><p>Skipstjóri (SS) þarf að vera handhafi gilds skipstjórnarskírteinis &lt;12m. og má einnig vera smáskipavélavörður (SSV) ef hann er hefur þau atvinnuréttindi, að öðrum kosti þarf auk þess að lögskrá smáskipavélavörð. Við lögskráningu er þá valin samsett staða Skipstjóri/smáskipavélavörður (SS/SSV):</p></div><br><br><br><h2 id="yfirlit-logskraningardaga" tabindex="-1">Yfirlit lögskráningardaga <a class="header-anchor" href="#yfirlit-logskraningardaga" aria-label="Permalink to &quot;Yfirlit lögskráningardaga&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">Hægt er að sækja um að fá upplýsingar um sína lögskráningardaga.</p><p><a href="https://innskraning.island.is/?id=eydubl.samgongustofa.is&amp;AuthID=cef48d4a-77a0-443f-aabf-5cfad1db602b" target="_blank" rel="noreferrer">Umsókn um lögskráningardaga</a></p></div><div class="info custom-block"><p class="custom-block-title">Útgerð getur óskað eftir því að láta Samgöngustofa lögskrá á eigið skip:</p><p><a href="https://eydublod.samgongustofa.is/27635895003040018549" target="_blank" rel="noreferrer">Lögskráning á vegum Samgöngustofu</a></p></div><br><br><br><h2 id="merking-takna" tabindex="-1">Merking tákna <a class="header-anchor" href="#merking-takna" aria-label="Permalink to &quot;Merking tákna&quot;">​</a></h2><figure><img src="'+a+'" class="icon-small"><figcaption>Lögskráð hefur verið í stöðuna</figcaption></figure><figure><img src="'+r+'" class="icon-small"><figcaption>Ekki er skylt að lögskrá í stöðuna</figcaption></figure><figure><img src="'+s+'" class="icon-small"><figcaption>Ekki er búið að lögskrá í stöðuna</figcaption></figure><p><span style="color:red;">*</span> Manna þarf þessa stöðu og lögskrá ef útivist skipsins fer yfir 14 klst, sbr. ákvæði sjómannalaga</p>',30),k=[l];function o(f,u,p,m,d,c){return t(),n("div",null,k)}const y=i(g,[["render",o]]);export{v as __pageData,y as default};
